import React, { useState } from 'react';
import OktaSignIn from '@okta/okta-signin-widget';

const Login = ({
  username,
  handleUsernameChange,
  roomName,
  handleRoomNameChange,
  handleSubmit
}) => {
  var OKTA_SETTINGS = {
    "baseUrl": `${process.env.REACT_APP_OKTA_ORG_URL}`,
    "clientId": `${process.env.REACT_APP_OKTA_CLIENT_ID}`,
    "issuer": `${process.env.REACT_APP_OKTA_ORG_URL}/oauth2/default`,
  };

  var okta = new OktaSignIn({
    baseUrl: OKTA_SETTINGS.baseUrl,
    clientId: OKTA_SETTINGS.clientId,
    authParams: {
      issuer: OKTA_SETTINGS.issuer,
      responseType: ["token", "id_token"],
      display: "page"
    }
  });

  const [isAuthenticated, setIsAuthenticated] = useState(false);

  const [user, setUser] = useState({
    email: '',
    firstName: '',
    lastName: '',
    login: '',
    mobilePhone: '',
    secondEmail: ''
  });

  const showLogin = () => {
    okta.remove();
    okta.renderEl({ "el" : "#okta-login-container" }, 
    function(res:any) {
      console.log(res);
    }, 
    function(err:any) {
      console.log(err);
    });
  };

  const logout = () => {
    okta.authClient.session.close();
    showLogin();
    setIsAuthenticated(false);
  }


  if (isAuthenticated) {
    return (
      <div className="App">
        <header className="App-header">
        <div>Hello {user.firstName} </div>
        <button onClick={logout}> logout</button>
        </header>
      </div>
    );
  } else {
    return (
      <div className="App" id="okta-login-container">
      </div>
    );
  }
};

export default Login;
